//selectorgadget.com/"},"SelectorGadget")),(0,r.kt)("pre",null,(0,r.kt)("code",{parentName:"pre",className:"language-javascript"},"const puppeteer = require('puppeteer');\n\nasync function scrape() {\n    const browser = await puppeteer.launch({\n        defaultViewport:\n        {\n            width:1920,\n            height:1080\n        }\n    });\n    const page = await browser.newPage();\n    await page.goto('https://www.google.com/search?q=roberto+bolano+books');\n\n    // Agree to cookies\n    let [button] = await page.$x(\"//button[contains(., 'I agree')]\");\n    if (button) {\n        await button.click();\n    }\n\n    const json = await page.evaluate(() => {\n        const carousel = extabar.querySelector('.DAVP1');\n\n        const scrapedData = {};\n        scrapedData[\"Books\"] = [];\n        const items = carousel.children;\n\n        // Function to depth first search an HTMLElement for a property\n        function getItemProperty(property) {\n            return function getProp(item) {\n                const childElementCount = item.childElementCount;\n                if(!item[property]) {\n                    if(childElementCount === 0) {\n                        return \"\";\n                    } else {\n                        for(let i=0; i<childElementCount; ++i) {\n                            const childLabel = getProp(item.children[i]);\n                            if(childLabel) {\n                                return childLabel;\n                            }\n                        }\n                    }\n\n                } else {\n                    return item[property];\n                }\n\n                return \"\";\n            }\n        }\n\n        function getAriaLabel(item) {\n            return getItemProperty('ariaLabel')(item);\n        }\n\n        function getTitle(item) {\n            return getItemProperty('title')(item);\n        }\n\n        for (const item of items) {\n            const label = getAriaLabel(item);\n\n            if(!label) {\n                continue;\n            }\n\n            const obj = {};\n\n            obj[\"name\"] = label;\n\n            const itemTitle = getTitle(item);\n\n            // Get the extensions data such as book year from the difference\n            // in item title and item aria-label\n            if(itemTitle.length > label.length) {\n                const details = itemTitle.substring(label.length + 2, itemTitle.length - 1);\n                obj[\"extensions\"] = details.split(', ');\n            }\n\n            scrapedData[\"Books\"].push(obj);\n        }\n\n        return JSON.stringify(scrapedData, null, 2);\n    });\n\n    console.log(json);\n    browser.close();\n}\n\nscrape();\n")),(0,r.kt)("h3",{id:"results"},"Results"),(0,r.kt)("p",null,"This manages to get us a JSON object containing all the book's showcased on Google's carousel. And you can imagine if you had a list of thousands of authors you could quickly get a huge database of books. Scraping pictures and links from the carousel is also possible which ",(0,r.kt)("a",{parentName:"p",href:"https://github.com/richhaar/code-challenge"},"I did here")," as part of a coding challenge. But the output of the previous code is as follows:"),(0,r.kt)("pre",null,(0,r.kt)("code",{parentName:"pre",className:"language-json"},'{\n  "Books": [\n    {\n      "name": "2666",\n      "extensions": [\n        "2004"\n      ]\n    },\n    {\n      "name": "The Savage Detectives",\n      "extensions": [\n        "1998"\n      ]\n    },\n    {\n      "name": "Distant Star",\n      "extensions": [\n        "1996"\n      ]\n    },\n    {\n      "name": "By Night in Chile",\n      "extensions": [\n        "2000"\n      ]\n    },\n    {\n      "name": "Last Evenings on Earth",\n      "extensions": [\n        "1997"\n      ]\n    },\n    {\n      "name": "Nazi Literature in the Americas",\n      "extensions": [\n        "1996"\n      ]\n    },\n    {\n      "name": "Amulet",\n      "extension